def first_part():
    return input_string.count('(') - input_string.count(')')


def second_part():
    floor = 0
    for pos, char in enumerate(input_string):
        floor += 1 if char == "(" else -1
        if floor < 0:
            return pos + 1


if __name__ == '__main__':
    print('First part: %d' % first_part())
    print('Second part: %d' % second_part())
